import React from 'react'
import _ from 'lodash'
import { manager, config } from 'acey'

export const withAcey = (App, Acey) => {
    const { manager, config } = Acey

    config.setEnvAsNextJS()
    config.done()
    const STORE_KEY = '_aceyStore'

    return class Wrap extends React.Component {

          constructor(props){
              super(props)
              !this.isServer() && manager.pendingHydrationStore().set(props.pageProps[STORE_KEY])
          }

          static getStaticProps = async ({ Component, router, ctx }) => {
            let pageProps = {}

            if (!ctx) throw new Error('No page context');
            const prevInitialPropsFunction = Component.getStaticProps

            if (Component.getStaticProps)
                pageProps = await Component.getStaticProps(ctx)

            Component.getStaticProps = (ctx) => pageProps
            Component.getStaticProps = prevInitialPropsFunction

            const ret = { 
                pageProps: { ...pageProps }
            }
            ret.pageProps[STORE_KEY] = manager.store().get()
            return ret
          }

          static getServerSideProps = async ({ Component, router, ctx }) => {
            let pageProps = {}

            if (!ctx) throw new Error('No page context');
            const prevInitialPropsFunction = Component.getServerSideProps

            if (Component.getServerSideProps)
                pageProps = await Component.getServerSideProps(ctx)

            Component.getServerSideProps = (ctx) => pageProps
            Component.getServerSideProps = prevInitialPropsFunction

            const ret = { 
                pageProps: { ...pageProps }
            }
            ret.pageProps[STORE_KEY] = manager.store().get()
            return ret
          }

          // static getInitialProps = async ({ Component, router, ctx }) => {
          //   let pageProps = {}

          // }

          isServer = () => typeof window === 'undefined'

          getClearedProps = () => {
              let newProps = {}
              for (let key in this.props){
                  if (key === 'pageProps'){
                      const copy = _.cloneDeep(this.props[key])
                      delete copy[STORE_KEY]
                      copy['isServer'] = this.isServer()
                      newProps[key] = copy
                  } else {
                      newProps[key] = this.props[key]
                  }
              }
              return newProps
          }
          render = () => <App {...this.getClearedProps()} />
      }
  }


const MyApp = props => {
  const { Component, pageProps } = props
  return (
    <Component {...pageProps} />
  )
}


export default withAcey(MyApp, {manager, config})
