import React from 'react'
import _ from 'lodash'
import * as Cookies from 'es-cookie'
import { config, store } from 'acey'

const MyApp = props => {
  const { Component, pageProps } = props
  return (
    <Component {...pageProps} />
  )
}

const STORE_KEY = '_aceyStore'
config.setEnvAsNextJS()

export const withAcey = (App) => {

    return class Wrap extends React.Component {

        constructor(props){
            super(props)

            this.hydrateClient()
        }

        hydrateClient = () => {
          if (this.isServer())
            return

          let toHydrate = {}
          for (let key in store.localStoreManager().getKeys()){
            const d = store.localStoreManager().getElement(key)
            if (d) toHydrate[key] = d
          }
          store.addPendingHydration(Object.assign({}, toHydrate, this.props.pageProps[STORE_KEY]))
        }

        static getInitialProps = async ({ Component, router, ctx }) => {
          if (!ctx) throw new Error('No page context');

          const hydrateAceyWithCookies = () => {
            const cookies = {}
            if (ctx.req && ctx.req.headers && ctx.req.headers.cookie) {
                const gotCookies = Cookies.parse(ctx.req.headers.cookie)
                for (let key in gotCookies){
                    if (store.exist(key))
                        cookies[key] = JSON.parse(gotCookies[key])
                }
                store.hydrateCookies(cookies)
            }
            return cookies
          }

          let pageProps = {}
          const cookies = hydrateAceyWithCookies()

          const prevInitialPropsFunction = Component.getInitialProps
          if (Component.getInitialProps)
              pageProps = await Component.getInitialProps(ctx)

          Component.getInitialProps = prevInitialPropsFunction

          return {
            pageProps: { 
              ...pageProps, 
              [STORE_KEY]: cookies 
            }
          }

        }

        isServer = () => typeof window === 'undefined'

        getClearedProps = () => {
            let newProps = {}
            for (let key in this.props){
                if (key === 'pageProps'){
                    const copy = _.cloneDeep(this.props[key])
                    delete copy[STORE_KEY]
                    copy['isServer'] = this.isServer()
                    newProps[key] = copy
                } else {
                    newProps[key] = this.props[key]
                }
            }
            return newProps
        }

        render = () => <App {...this.getClearedProps()} />
    }
}

export default withAcey(MyApp)
