import { useAcey } from 'acey'
import { Counter, Todolist, User } from '../src/models'


const Home = (props) => {

  const counters = [Counter, User.counter()]
  const todolist = [Todolist, User.todolist()]

  useAcey([
    Counter,
    Todolist,
    User
  ])

  const method = (list = [], method = '', then = '' | 'coookie' | 'localStore') => {
    list.forEach((v) => {
      const ret = v[method]()
      then && ret[then]()
    })
  }

  const increment = (then = '' | 'coookie' | 'localStore') => method(counters, 'increment', then)
  const decrement = (then = '' | 'coookie' | 'localStore') => method(counters, 'decrement', then)
  const addTodo = (then = '' | 'coookie' | 'localStore') =>  method(todolist, 'add', then)

  const clearAllCookie = () => {
    Todolist.removeCookies()
    Counter.removeCookies()
    User.removeCookies()
    location.reload();
  }

  const clearLocalStore = () => {
    Todolist.removeLocalStore()
    Counter.removeLocalStore()
    User.removeLocalStore()
    location.reload();
  }

  return (
    <div className="container">
      <button onClick={() => decrement()}>decrement</button>
      {counters.map((v, index) => <span style={{marginLeft: 10, marginRight: 10}} key={index}>{v.get()}</span>)}
      <button onClick={() => increment()}>increment</button>
      <br />


      <button onClick={clearAllCookie}>clear cookies</button>
      <button onClick={clearLocalStore}>clear local stores</button>
      <div>
        {Todolist.map((v, index) => {
          return (
            <div key={index}>
              <span>{v.content()} : </span>
              <span>{v.ID()}</span>
            </div>
          )
        })}
      </div>
    </div>
  )
}

Home.getInitialProps = ({ query }) => {
  //Counter.increment()
  return {
    id: 10
  }
}

export default Home
