import { useAcey } from 'acey'
import { Counter, Todolist, User } from '../src/models'


const Home = (props) => {

  const counters = [Counter, User.counter()]
  const todolist = [Todolist, User.todolist()]

  useAcey([
    Counter,
    Todolist,
    User
  ])

  const method = (list = [], method = '', then = '' | 'coookie' | 'localStore') => {
    list.forEach((v) => {
      const ret = v[method]()
      then && ret[then]()
    })
  }

  const increment = (then = '' | 'coookie' | 'localStore') => method(counters, 'increment', then)
  const decrement = (then = '' | 'coookie' | 'localStore') => method(counters, 'decrement', then)
  const addTodo = (then = '' | 'coookie' | 'localStore') =>  method(todolist, 'add', then)


  return (
    <div className="container">
      <button onClick={() => decrement()}>decrement</button>
      {counters.map((v, index) => {
        return <p key={index}>{v.get()}</p>
      })}
      <button onClick={() => increment()}>increment</button>

      {/* <button onClick={add}>add</button>
